import React, {useState, useEffect} from 'react';
import { Link } from 'react-router-dom';
import { Button } from './Button';
import './Navbar.css';

function Navbar(props) {

    let content = {
        English: {
          title: "Home",
          title2: "Menu",
          title3: "Values",
          button: "ORDER"
        },
        French: {
          title: "Accueil",
          title2: "Menu",
          title3: "Valeurs",
          button: "COMMANDER"
        }
      };

      props.language === "French"
        ? (content = content.French)
        : (content = content.English);

        let languageStoredInLocalStorage = localStorage.getItem("language");
        let [language, setLanguage] = useState(
          languageStoredInLocalStorage ? languageStoredInLocalStorage : "English"
        );

    const [click, setClick] = useState(false);
    const [button, setButton] = useState(true);

    const handleClick = () => setClick(!click);
    const closeMobileMenu = () => setClick(false);

    const showButton = () => {
        if (window.innerWidth <= 960) {
            setButton(false);
        } else {
            setButton(true);
        }
    };


    useEffect(() => {
        showButton();
    }, []);

    window.addEventListener('resize',showButton);

    return (
    <>
        <nav className='navbar'>
            <div className='navbar-container'>
                <Link to='/' className='navbar-logo' onClick={closeMobileMenu}>
                    LOGO <i class="fa-solid fa-cookie"></i>
                </Link>
                <div className='menu-icon' onClick={handleClick}>
                    <i className={click ? 'fas fa-times' : 'fas fa-bars'}/>
                </div>
                <ul className={click ? 'nav-menu active' : 'nav-menu'}>
                    <li className='nav-item'>
                        <Link to='/' className='nav-links' onClick={closeMobileMenu}>
                        {content.title}
                        </Link>
                    </li>
                    <li className='nav-item'>
                        <Link to='/menu' className='nav-links' onClick={closeMobileMenu}>
                        {content.title2}
                        </Link>
                    </li>
                    <li className='nav-item'>
                        <Link to='/values' className='nav-links' onClick={closeMobileMenu}>
                        {content.title3}
                        </Link>
                    </li>
                    <li className='nav-item' handleSetLanguage={language => {
                        setLanguage(language);
                        storeLanguageInLocalStorage(language);
                        }}>
                        <select className="custom-select" value={props.language}
                            onChange={e => props.handleSetLanguage(e.target.value)}>
                            <option value="English">English</option>
                            <option value="Français">Français</option>
                        </select>
                    </li>
                </ul>
                {button && <Button buttonStyle='btn--outline'>{content.button}</Button>}
            </div>
        </nav>
    </>
  )
}

function storeLanguageInLocalStorage(language) {
    localStorage.setItem("language", language);
  }

export default Navbar;
